import React from 'react';
import { StaticQuery, graphql, Link } from 'gatsby';
import Img from 'gatsby-image';

export default () => (
    <StaticQuery
        query={graphql`
            query EventsLandingQuery {
                allMarkdownRemark(
                    filter: { fileAbsolutePath: { regex: "/events/.*md$/" } }
                    sort: { fields: [frontmatter___date], order: DESC }
                    limit: 3
                ) {
                    edges {
                        node {
                            id
                            frontmatter {
                                path
                                cover {
                                    publicURL
                                    childImageSharp {
                                        fluid(maxWidth: 1000) {
                                            srcSet
                                            ...GatsbyImageSharpFluid_tracedSVG
                                        }
                                    }
                                }
                                title
                                date
                                author
                            }
                        }
                    }
                }
            }
        `}
        render={data => (
            <div className='event-sect'>
                {/* Tailwind css example */}
                <div className='event-text text-center w-full'>
                    <h1 class='underline-small'>Latest Events</h1>
                </div>
                <div className='event-latest'>
                    {data.allMarkdownRemark.edges.map(post => (
                        <Link
                            to={post.node.frontmatter.path}
                            key={post.node.id}
                            className='event-list-link'>
                            <div key={post.node.id} className='event-card'>
                                <Img
                                    className='event-img'
                                    fluid={
                                        post.node.frontmatter.cover
                                            .childImageSharp.fluid
                                    }
                                />
                                <center>
                                    <h3>
                                        {post.node.frontmatter.title.length > 25
                                            ? `${post.node.frontmatter.title.slice(
                                                  0,
                                                  27
                                              )}...`
                                            : post.node.frontmatter.title}
                                    </h3>
                                    <img
                                        alt=''
                                        src='data:image/png;base64,'
                                    />
                                    <span>{post.node.frontmatter.date}</span>
                                </center>
                            </div>
                        </Link>
                    ))}
                </div>
            </div>
        )}
    />
);
